import styled, {keyframes, ThemeProvider} from 'styled-components';
import theme from '../../styles/themeStyledComponents';
import React from 'react';

const Container = styled.div`
    height: 100vh;
    width: 100%;
    margin: auto;
    font-family: Arial, Helvetica, sans-serif;
    background-color: #000;
    overflow: hidden;
`;

const move = keyframes`
  from {
    background-position: -2px -2px, -2px -2px, -1px -1px, -1px -1px;
  }

  to {
    background-position: -2px -2px,-2px -2px,-1px 20px,-1px -1px;
  }
`;
const phantomWalk = keyframes`
  from {
    background-position: 0px;
  }

  to {
    background-position: 26px 0px;
  }
`;

const Floor = styled.div`
    height: 100px;
    width: 100px;
    margin: auto;
    background-color: #000;
    background-image: linear-gradient(cyan 0px, transparent 0px),
        linear-gradient(90deg, cyan 4px, transparent 0px),
        linear-gradient(cyan 1px, transparent 1px),
        linear-gradient(90deg, cyan 1px, transparent 1px);
    background-size: 20% 20%, 20% 20%, 20% 5%, 20% 20%;
    background-position: -2px -2px, -2px -2px, -1px -9px, -1px -1px;
    transform-style: preserve-3d;
    /* transform: perspective(125px) rotateX(45deg); */
    /* animation: ${move} 0.5s linear infinite; */
`;
const Square = styled.div``;
// EXTENDING STYLES

// animación

const DaVueltas = styled.div`
    width: 64px;
    height: 64px;
    background-color: red;
`;

const Phantom = styled.div`
    margin: auto;
    width: 90px;
    overflow: hidden;
`;
const PhantomHead = styled.div`
    border-top-left-radius: 90px;
    border-top-right-radius: 90px;
    height: 45px;
    width: 90px;
    background: #e05c5c;
    margin: 0px auto;
`;
const PhantomBody = styled.div`
    height: 30px;
    width: 90px;
    margin: 0px auto;
    background: #e05c5c;
`;
const PhantomFooter = styled.div`
    width: 150px;
    height: 27px;
    background: linear-gradient(#e05c5c 40%, rgba(255, 255, 255, 0) 0px) 0px 0px,
        radial-gradient(circle closest-side, #e05c5c 100%, rgba(0, 0, 0, 0) 0) 100px 0px transparent;
    background-size: 26px 26px;
    background-repeat: repeat-x;
    animation: ${phantomWalk} 0.5s linear infinite;
`;

class Main extends React.Component {
    constructor(props) {
        super(props);
        this.state = {};
    }

    render() {
        return (
            <Container>
                <Floor></Floor>
                <PhantomFooter></PhantomFooter>
                <Phantom>
                    <PhantomHead></PhantomHead>
                    <PhantomBody></PhantomBody>
                    <PhantomFooter></PhantomFooter>
                </Phantom>
            </Container>
        );
    }
}

export default Main;
